import $ from "jquery";
import { useRef, useState } from "react";
import axios from "axios";
import port from "./../../data/port.json";
import { useCookies, useEffect } from "react-cookie";

const SocialSignUp = () => {
  const [cookiesAuth, setCookieAuth, removeCookieAuth] = useCookies(["auth"]);

  const emailRef = useRef();

  const [errorMessage, setErrorMessage] = useState("");

  const [signUpData, setSignUpData] = useState({
    email: "",
    password: "",
    rePassword: "",
    name: "",
  });

  const onChangeSignUpData = (e) => {
    setSignUpData({

    })
  }


  useEffect(() => {
    console.log(cookiesAuth.auth);
  }, []);

  const onClickSignUpButton = () => {
    if (signUpData.email === "") {
      alert("이메일을 입력해주세요.");
      emailRef.current.focus();
      return;
    }

    if (signUpData.password === "") {
      alert("비밀번호를 입력해주세요.");
      $("#password").focus();
      return;
    }

    if (signUpData.rePassword === "") {
      alert("비밀번호 확인을 입력해주세요.");
      $("#rePassword").focus();
      return;
    }

    if (signUpData.name === "") {
      alert("이름을 입력해주세요.");
      $("#name").focus();
      return;
    }

    if (signUpData.password !== signUpData.rePassword) {
      alert("비밀번호와 비밀번호 확인이 같지 않습니다.");
      setSignUpdata({
        ...signUpData,
        password: "",
        rePassword: "",
      });
      $("#password").focus();
      return;
    }

    sendSignUpData()
      .then((res) => {
        console.log(res.data);
        alert(res.data.result);
        window.location.reload();
      })
      .catch((e) => {
        console.log(e);
        setErrorMessage(e.response.data.error);
      });
  };

  const sendSignUpData = async () => {
    return await axios.post(port.url + "/user/signUp", signUpData);
  };

  return (
    <main>
    <section className="py-5 text-center container">
        <div className="row py-lg-5">
            <div className="col-lg-6 col-md-8 mx-auto">
                <h1 className="fw-light">Movie</h1>
                <p className="lead text-muted">리뷰하고 싶은 영화를 추가하고,
                    별점을 주세요.<br />
                    Create Page
                </p>
            </div>
        </div>
    </section>

</main>

    
  );
};

export default SocialSignUp;
